import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { ReactComponent as RainbowSVG } from './img/rainbow.svg'; // Correct path
import { ReactComponent as CloseSVG } from './img/x.svg'; // Adjust path if needed

// Styled components for the Main Page

const Page = styled.div`
    width: 100%;
    height: 100%;
    margin: 0 auto;
    padding: 0;
    background-color: #F7F7F7;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    box-sizing: border-box;
    position: relative; /* Added for absolute positioning */
    padding-bottom: 710px; /* Adjust this value as needed */
`;

const LogoutButton = styled.button`
    width: 200px;
    height: 50px;
    border: none;
    font-weight: 700;
    font-size: 16px;
    background-color: #8528d4;
    border-radius: 24px;
    color: white;
    cursor: pointer;
    transition: background-color 0.3s;
    position: absolute; /* Positioning the button absolutely */
    top: 20px; /* Distance from the top */
    right: 20px; /* Distance from the right */

    &:hover {
        background-color: #5d1a90;
    }
`;

const Box = styled.div`
    background-color: white;
    border-radius: 10px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    padding: 40px; /* Increased padding for larger content area */
    text-align: center;
    max-width: 800px; /* Increased width */
    width: 100%; /* Make the box full width up to max-width */
    position: relative; /* Positioning the box relatively to place the close button */
    left: 20px; /* Distance from the left side */
    top: 50%; /* Center vertically on the page */
    transform: translateY(-50%); /* Adjust vertical alignment */

    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 20px;
`;

const StyledRainbowSVG = styled(RainbowSVG)`
    margin-top: 20px; /* Adjust this value to move the SVG down */
`;

const PromoText = styled.p`
    font-size: 20px;
    font-weight: 500;
    color: #262626;
    margin: 0; /* Reset margin for consistency */
`;

const TestButton = styled.button`
    width: 180px;
    height: 40px;
    border: none;
    font-weight: 700;
    font-size: 14px;
    background-color: #28a745;
    border-radius: 24px;
    color: white;
    cursor: pointer;
    transition: background-color 0.3s;

    &:hover {
        background-color: #218838;
    }
`;

const CloseButton = styled(CloseSVG)`
    position: absolute;
    top: 10px; /* Distance from the top of the box */
    right: 10px; /* Distance from the right side of the box */
    cursor: pointer;
    width: 24px; /* Adjust size as needed */
    height: 24px; /* Adjust size as needed */
`;

// Main component
const Main = () => {
    const navigate = useNavigate();

    const handleLogout = () => {
        // Handle logout functionality
        navigate('/signin'); // Redirect to sign-in page or wherever necessary
    };

    const handleTestClick = () => {
        // Handle the "받아보기" button click
        alert('폴스널 컬러 테스트를 시작합니다!'); // Replace with actual test functionality
    };

    const handleCloseClick = () => {
        // Handle the close button click
        alert('Close button clicked!'); // Replace with actual close functionality
    };

    return (
        <Page>
            <Box>
                <CloseButton onClick={handleCloseClick} />
                <StyledRainbowSVG width={148} height={107} />
                <PromoText>당신의 폴스널 컬러 테스트를 받아보시겠습니까?</PromoText>
                <TestButton onClick={handleTestClick}>받아보기</TestButton>
            </Box>
            <LogoutButton onClick={handleLogout}>Logout</LogoutButton>
        </Page>
    );
};

export default Main;
